import React from "react"
import { Drawer, Button, Row, Col, Avatar } from "antd"
import { useState } from "react"
import styled from "styled-components"
import { FC } from "react"
import { MenuOutlined, MenuUnfoldOutlined, UserOutlined } from "@ant-design/icons"

const dummy_me = {
  email: "scscsc@naver.com",
  nickname: "김건모",
}

export const CustomPaddingRow = styled(Row)`
  padding: ${(props) => props.padding || "0px"};
`

const Input = styled(Row).attrs((props) => ({
  padding: props.padding || "1em",
}))`
  color: palevioletred;
  font-size: 1em;
  border: 2px solid palevioletred;
  border-radius: 3px;

  /* here we use the dynamically computed prop */
  margin: ${(props) => props.size};
  padding: ${(props) => props.size};
`

export const BackgroundDiv = styled.div`
  background-color: #fafafa;
  height: 90vh;
`

const MyLayout: FC<{ children: any }> = ({ children }) => {
  const [visible, setVisible] = useState(false)
  const onDrawerClose = () => {
    setVisible(false)
  }
  const onClickMenu = () => {
    setVisible(true)
  }
  return (
    <>
      <Drawer
        title='User'
        placement='left'
        closable={false}
        onClose={onDrawerClose}
        visible={visible}
      >
        <h2>{dummy_me?.email}</h2>
        <h3>{dummy_me?.nickname + "님"}</h3>
        <Button style={{ margin: "5px" }}>내 정보</Button>
        <Button style={{ margin: "5px" }}>로그아웃</Button>
      </Drawer>
      <Row>
        <Col span={4}>
          <Button size='large' onClick={onClickMenu}>
            <MenuUnfoldOutlined />
          </Button>
        </Col>
        <Col span={16}></Col>
        <Col span={4}>
          <Avatar shape='square' size='large' icon={<UserOutlined />} />
        </Col>
      </Row>
      <BackgroundDiv>{children}</BackgroundDiv>
    </>
  )
}

export default MyLayout
